mapper.select:
SELECT c.id, c.code, c.created_at, c.start_date, c.end_date, c.status, c.amount, c.bank_name, c.bank_account, c.payment_day, c.deposit_owner, c.relationship, c.payment_email, c.is_deleted, c.etc, e.name AS employee_name, cu.id AS customer_id, cu.name AS customer_name, GROUP_CONCAT(p.name SEPARATOR ', ') AS product_names FROM contract c JOIN employee e ON c.employee_id = e.id JOIN customer cu ON c.customer_id = cu.id LEFT JOIN contract_product cp ON c.id = cp.contract_id LEFT JOIN product p ON cp.product_id = p.id
NOT c.is_deleted AND c.employee_id = #{employeeId}
AND c.amount >= #{search.minAmount}
AND c.amount <= #{search.maxAmount}
AND c.code LIKE CONCAT('%', #{search.code}, '%')
AND c.created_at >= #{search.createdAtStart}
AND c.created_at <= #{search.createdAtEnd}
AND c.start_date >= #{search.startDateStart}
AND c.start_date <= #{search.startDateEnd}
AND c.end_date >= #{search.endDateStart}
AND c.end_date <= #{search.endDateEnd}
AND c.status = #{search.status}
AND c.bank_name LIKE CONCAT('%', #{search.bankName}, '%')
AND c.payment_day >= #{search.paymentDayStart}
AND c.payment_day <= #{search.paymentDayEnd}
AND c.deposit_owner LIKE CONCAT('%', #{search.depositOwner}, '%')
AND c.relationship = #{search.relationship}
AND e.name LIKE CONCAT('%', #{search.employeeName}, '%')
AND cu.name LIKE CONCAT('%', #{search.customerName}, '%')
AND EXISTS ( SELECT 1 FROM contract_product cp2 JOIN product p2 ON cp2.product_id = p2.id WHERE cp2.contract_id = c.id AND p2.name LIKE CONCAT('%', #{search.productName}, '%') )
GROUP BY c.id

SELECT COUNT(id)
FROM contract
WHERE id = #{id}
