mapper.select:
SELECT dt.id, dt.name, dt.description, dt.version, dt.created_at, fu.path AS upload_file_path FROM document_template dt JOIN file_upload fu ON dt.file_upload_id = fu.id WHERE dt.is_deleted = FALSE
AND dt.name LIKE CONCAT('%', #{name}, '%')
AND dt.version LIKE CONCAT('%', #{version}, '%')
AND dt.created_at >= #{createdAtStart}
AND dt.created_at <= #{createdAtEnd}
ORDER BY dt.created_at DESC
