select:
SELECT id , sales_date , department , amount , employee_name , contract_id FROM sales WHERE is_deleted = false
AND sales_date >= #{startDate}
AND sales_date <= #{endDate}
AND amount >= #{minAmount}
AND amount <= #{maxAmount}
AND department LIKE CONCAT('%', #{department}, '%')
AND employee_name LIKE CONCAT('%', #{employeeName}, '%')
AND contract_id = #{contractId}
ORDER BY sales_date DESC
